function santaWalk(input) {
  let xCoordinate = 0;
  let yCoordinate = 0;
  const houseMap = {};
  for (i = 0; i <= input.length; i++) {
    const address = `house${xCoordinate}x${yCoordinate}`;
    if (address in houseMap) {
      houseMap[address]++;
    } else {
      houseMap[address] = 1;
    }
    switch (input[i]) {
      case ">":
        xCoordinate++;
        break;
      case "<":
        xCoordinate--;
        break;
      case "^":
        yCoordinate++;
        break;
      case "v":
        yCoordinate--;
        break;
      default:
        break;
    }
    //    console.log(houseMap);
  }
  houseTotal = Object.keys(houseMap).length;
  console.log(houseTotal);
  return houseTotal;
}

function roboWalk(input) {
  let xCoordinateSanta = 0;
  let yCoordinateSanta = 0;
  let xCoordinateRobot = 0;
  let yCoordinateRobot = 0;
  let address = "house0x0";
  const houseMap = { house0x0: 2 };
  for (i = 0; i <= input.length; i++) {
    if (i % 2 === 0) {
      switch (input[i]) {
        case ">":
          xCoordinateSanta++;
          break;
        case "<":
          xCoordinateSanta--;
          break;
        case "^":
          yCoordinateSanta++;
          break;
        case "v":
          yCoordinateSanta--;
          break;
        default:
          break;
      }
      address = `house${xCoordinateSanta}x${yCoordinateSanta}`;
    } else {
      switch (input[i]) {
        case ">":
          xCoordinateRobot++;
          break;
        case "<":
          xCoordinateRobot--;
          break;
        case "^":
          yCoordinateRobot++;
          break;
        case "v":
          yCoordinateRobot--;
          break;
        default:
          break;
      }
      address = `house${xCoordinateRobot}x${yCoordinateRobot}`;
    }

    if (address in houseMap) {
      houseMap[address]++;
    } else {
      houseMap[address] = 1;
    }

    //    console.log(houseMap);
  }
  houseTotal = Object.keys(houseMap).length;
  console.log(houseTotal);
  return houseTotal;
}

